#! /usr/bin/perl -w
# STATUS: ERR
# TEST: ./rwsiteinfo --delimited --fields=class,type,sensor --sensors=@ --site-config-file ./tests/rwsiteinfo-site.conf

use strict;
use SiLKTests;

my $rwsiteinfo = check_silk_app('rwsiteinfo');

my $site_info = "$SiLKTests::srcdir/tests/rwsiteinfo-site.conf";
unless (-f $site_info) {
    skip_test("Did not find file '$site_info'");
}

my $cmd = "$rwsiteinfo --delimited --fields=class,type,sensor --sensors=\@ --site-config-file $SiLKTests::srcdir/tests/rwsiteinfo-site.conf";

exit (check_exit_status($cmd) ? 1 : 0);
